import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function StudentDetailsPage() {
  const { id } = useParams(); // ID do aluno
  const navigate = useNavigate();

  const [student, setStudent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("info");

  // --- Treinos ---
  const [workouts, setWorkouts] = useState([]);
  const [loadingWorkouts, setLoadingWorkouts] = useState(false);

  // --- Exerc√≠cios (modal) ---
  const [showExercisesModal, setShowExercisesModal] = useState(false);
  const [selectedWorkout, setSelectedWorkout] = useState(null);
  const [exercises, setExercises] = useState([]);
  const [loadingExercises, setLoadingExercises] = useState(false);
  const [exerciseForm, setExerciseForm] = useState({
    name: "",
    series: "",
    repetitions: "",
    load: "",
    restTime: "",
    notes: "",
  });

  const token = localStorage.getItem("token");

  // ============= BUSCAR ALUNO =============
  useEffect(() => {
    const fetchStudent = async () => {
      try {
        const res = await fetch(`http://localhost:3333/students/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        if (res.ok) setStudent(data);
        else setError(data?.error || "Erro ao carregar informa√ß√µes do aluno");
      } catch (err) {
        setError("Erro ao carregar informa√ß√µes do aluno");
      } finally {
        setLoading(false);
      }
    };
    fetchStudent();
  }, [id, token]);

  // ============= BUSCAR TREINOS =============
  useEffect(() => {
    const fetchWorkouts = async () => {
      try {
        setLoadingWorkouts(true);
        const res = await fetch(`http://localhost:3333/workouts/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        if (Array.isArray(data)) setWorkouts(data);
      } catch (err) {
        console.error("Erro ao buscar treinos:", err);
      } finally {
        setLoadingWorkouts(false);
      }
    };
    fetchWorkouts();
  }, [id, token]);

  // ============= CRIAR TREINO =============
  const handleAddWorkout = async () => {
    const title = prompt("T√≠tulo do treino:");
    const description = prompt("Descri√ß√£o do treino:");
    const dayOfWeek = prompt("Dia da semana (ex: segunda):");
    if (!title) return;

    try {
      const res = await fetch(`http://localhost:3333/workouts/${id}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ title, description, dayOfWeek }),
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data?.error || "Erro ao criar treino");
      setWorkouts((prev) => [...prev, data.workout]);
      alert("Treino criado com sucesso!");
    } catch (err) {
      console.error(err);
      alert(err.message);
    }
  };

  // ============= EXERC√çCIOS: ABRIR MODAL + LISTAR =============
  const openExercisesModal = async (workout) => {
    setSelectedWorkout(workout);
    setShowExercisesModal(true);
    setExercises([]);
    setLoadingExercises(true);
    try {
      const res = await fetch(
        `http://localhost:3333/exercises/${workout.id}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const data = await res.json();
      if (Array.isArray(data)) setExercises(data);
      else setExercises([]); // se a API devolver objeto, evita quebrar
    } catch (err) {
      console.error("Erro ao buscar exerc√≠cios:", err);
    } finally {
      setLoadingExercises(false);
    }
  };

  const closeExercisesModal = () => {
    setShowExercisesModal(false);
    setSelectedWorkout(null);
    setExerciseForm({
      name: "",
      series: "",
      repetitions: "",
      load: "",
      restTime: "",
      notes: "",
    });
  };

  // ============= CRIAR EXERC√çCIO =============
  const handleExerciseChange = (e) => {
    setExerciseForm({ ...exerciseForm, [e.target.name]: e.target.value });
  };

  const handleCreateExercise = async (e) => {
    e.preventDefault();
    if (!selectedWorkout) return;

    try {
      const res = await fetch(
        `http://localhost:3333/exercises/${selectedWorkout.id}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          body: JSON.stringify({
            name: exerciseForm.name,
            series: exerciseForm.series ? Number(exerciseForm.series) : null,
            repetitions: exerciseForm.repetitions || null,
            load: exerciseForm.load ? Number(exerciseForm.load) : null,
            restTime: exerciseForm.restTime || null,
            notes: exerciseForm.notes || null,
          }),
        }
      );
      const data = await res.json();
      if (!res.ok) throw new Error(data?.error || "Erro ao criar exerc√≠cio");
      // a API geralmente retorna o exerc√≠cio criado; se n√£o, voc√™ pode recarregar a lista
      setExercises((prev) => [...prev, data.exercise || data]); 
      setExerciseForm({
        name: "",
        series: "",
        repetitions: "",
        load: "",
        restTime: "",
        notes: "",
      });
    } catch (err) {
      console.error(err);
      alert(err.message);
    }
  };

  if (loading) return <p className="p-4">Carregando...</p>;
  if (error) return <p className="p-4 text-danger">{error}</p>;

  return (
    <div className="container mt-4">
      <button className="btn btn-light mb-3" onClick={() => navigate(-1)}>
        ‚¨Ö Voltar
      </button>

      <div className="card">
        <div className="card-body">
          <h4>{student.name}</h4>

          {/* Abas */}
          <ul className="nav nav-tabs mt-3">
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === "info" ? "active" : ""}`}
                onClick={() => setActiveTab("info")}
              >
                üìÑ Informa√ß√µes
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === "treinos" ? "active" : ""}`}
                onClick={() => setActiveTab("treinos")}
              >
                üèãÔ∏è Treinos
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === "feedbacks" ? "active" : ""}`}
                onClick={() => setActiveTab("feedbacks")}
              >
                üí¨ Feedbacks
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === "progresso" ? "active" : ""}`}
                onClick={() => setActiveTab("progresso")}
              >
                üìà Progresso
              </button>
            </li>
          </ul>

          {/* Conte√∫do das abas */}
          <div className="mt-3">
            {activeTab === "info" && (
              <div>
                <p><strong>Email:</strong> {student.email}</p>
                <p><strong>Telefone:</strong> {student.phone || "-"}</p>
                <p><strong>Idade:</strong> {student.age || "-"}</p>
                <p><strong>Peso:</strong> {student.weight ? `${student.weight} kg` : "-"}</p>
                <p><strong>Altura:</strong> {student.height ? `${student.height} m` : "-"}</p>
                <p><strong>Objetivo:</strong> {student.goal || "-"}</p>
              </div>
            )}

            {/* TREINOS */}
            {activeTab === "treinos" && (
              <div>
                <div className="d-flex justify-content-between align-items-center">
                  <h5>üèãÔ∏è Treinos</h5>
                  <button className="btn btn-outline-primary" onClick={handleAddWorkout}>
                    + Novo Treino
                  </button>
                </div>

                {loadingWorkouts ? (
                  <p className="mt-2">Carregando treinos...</p>
                ) : workouts.length === 0 ? (
                  <p className="mt-2">Nenhum treino cadastrado.</p>
                ) : (
                  <div className="list-group mt-2">
                    {workouts.map((w) => (
                      <button
                        key={w.id}
                        type="button"
                        className="list-group-item list-group-item-action"
                        onClick={() => openExercisesModal(w)}
                      >
                        <div className="d-flex justify-content-between">
                          <div>
                            <strong>{w.title}</strong>{" "}
                            <span className="text-muted">
                              ‚Äî {w.dayOfWeek || "Dia n√£o definido"}
                            </span>
                            {w.description && (
                              <div><small>{w.description}</small></div>
                            )}
                          </div>
                          <span className="text-primary">Ver exerc√≠cios ‚ñ∏</span>
                        </div>
                      </button>
                    ))}
                  </div>
                )}
              </div>
            )}

            {activeTab === "feedbacks" && <p>Em breve‚Ä¶</p>}
            {activeTab === "progresso" && <p>Em breve‚Ä¶</p>}
          </div>
        </div>
      </div>

      {/* MODAL DE EXERC√çCIOS */}
      {showExercisesModal && selectedWorkout && (
        <div
          className="modal fade show d-block"
          tabIndex="-1"
          style={{ backgroundColor: "rgba(0,0,0,0.6)" }}
        >
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  Exerc√≠cios ‚Äî {selectedWorkout.title}
                </h5>
                <button className="btn-close" onClick={closeExercisesModal} />
              </div>

              <div className="modal-body">
                {/* Lista de exerc√≠cios */}
                <h6 className="mb-2">Lista</h6>
                {loadingExercises ? (
                  <p>Carregando exerc√≠cios...</p>
                ) : exercises.length === 0 ? (
                  <p>Nenhum exerc√≠cio cadastrado para este treino.</p>
                ) : (
                  <div className="table-responsive">
                    <table className="table table-sm">
                      <thead>
                        <tr>
                          <th>Exerc√≠cio</th>
                          <th>S√©ries</th>
                          <th>Reps</th>
                          <th>Carga (kg)</th>
                          <th>Descanso</th>
                          <th>Obs</th>
                        </tr>
                      </thead>
                      <tbody>
                        {exercises.map((ex) => (
                          <tr key={ex.id}>
                            <td>{ex.name}</td>
                            <td>{ex.series || "-"}</td>
                            <td>{ex.repetitions || "-"}</td>
                            <td>{ex.load ?? "-"}</td>
                            <td>{ex.restTime || "-"}</td>
                            <td>{ex.notes || "-"}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}

                <hr />

                {/* Formul√°rio de novo exerc√≠cio */}
                <h6 className="mb-2">Adicionar exerc√≠cio</h6>
                <form onSubmit={handleCreateExercise}>
                  <div className="row g-2">
                    <div className="col-md-4">
                      <input
                        className="form-control"
                        placeholder="Nome* (ex: Supino reto)"
                        name="name"
                        value={exerciseForm.name}
                        onChange={handleExerciseChange}
                        required
                      />
                    </div>
                    <div className="col-md-2">
                      <input
                        className="form-control"
                        placeholder="S√©ries"
                        name="series"
                        type="number"
                        value={exerciseForm.series}
                        onChange={handleExerciseChange}
                      />
                    </div>
                    <div className="col-md-2">
                      <input
                        className="form-control"
                        placeholder="Reps (ex: 10-12)"
                        name="repetitions"
                        value={exerciseForm.repetitions}
                        onChange={handleExerciseChange}
                      />
                    </div>
                    <div className="col-md-2">
                      <input
                        className="form-control"
                        placeholder="Carga (kg)"
                        name="load"
                        type="number"
                        step="0.1"
                        value={exerciseForm.load}
                        onChange={handleExerciseChange}
                      />
                    </div>
                    <div className="col-md-2">
                      <input
                        className="form-control"
                        placeholder="Descanso (ex: 60s)"
                        name="restTime"
                        value={exerciseForm.restTime}
                        onChange={handleExerciseChange}
                      />
                    </div>
                    <div className="col-12 mt-2">
                      <input
                        className="form-control"
                        placeholder="Observa√ß√µes (opcional)"
                        name="notes"
                        value={exerciseForm.notes}
                        onChange={handleExerciseChange}
                      />
                    </div>
                  </div>

                  <div className="mt-3 d-flex justify-content-end">
                    <button
                      type="button"
                      className="btn btn-secondary me-2"
                      onClick={closeExercisesModal}
                    >
                      Fechar
                    </button>
                    <button type="submit" className="btn btn-success">
                      Salvar exerc√≠cio
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
