import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

export default function StudentDetailsPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [student, setStudent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("info");

  // Estados dos treinos
  const [workouts, setWorkouts] = useState([]);
  const [loadingWorkouts, setLoadingWorkouts] = useState(false);

  // Buscar informa√ß√µes do aluno
  useEffect(() => {
    const fetchStudent = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await fetch(`http://localhost:3333/students/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await response.json();

        if (response.ok) {
          setStudent(data);
        } else {
          setError("Erro ao carregar informa√ß√µes do aluno");
        }
      } catch (err) {
        setError("Erro ao carregar informa√ß√µes do aluno");
      } finally {
        setLoading(false);
      }
    };

    fetchStudent();
  }, [id]);

  // Buscar treinos do aluno
  useEffect(() => {
    const fetchWorkouts = async () => {
      try {
        setLoadingWorkouts(true);
        const token = localStorage.getItem("token");
        const response = await fetch(`http://localhost:3333/workouts/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await response.json();
        if (Array.isArray(data)) setWorkouts(data);
      } catch (err) {
        console.error("Erro ao buscar treinos:", err);
      } finally {
        setLoadingWorkouts(false);
      }
    };

    fetchWorkouts();
  }, [id]);

  // Criar novo treino
  const handleAddWorkout = async () => {
    const title = prompt("T√≠tulo do treino:");
    const description = prompt("Descri√ß√£o do treino:");
    const dayOfWeek = prompt("Dia da semana (ex: segunda):");

    if (!title) return;

    try {
      const token = localStorage.getItem("token");
      const response = await fetch(`http://localhost:3333/workouts/${id}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ title, description, dayOfWeek }),
      });

      const data = await response.json();
      if (response.ok) {
        alert("Treino criado com sucesso!");
        setWorkouts((prev) => [...prev, data.workout]);
      } else {
        alert(data.error || "Erro ao criar treino");
      }
    } catch (err) {
      alert("Erro ao criar treino");
      console.error(err);
    }
  };

  if (loading) return <p>Carregando...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="container mt-4">
      <button className="btn btn-light mb-3" onClick={() => navigate(-1)}>
        ‚¨Ö Voltar
      </button>

      <div className="card">
        <div className="card-body">
          <h4>{student.name}</h4>

          {/* Abas */}
          <ul className="nav nav-tabs mt-3">
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === "info" ? "active" : ""}`}
                onClick={() => setActiveTab("info")}
              >
                üìÑ Informa√ß√µes
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === "treinos" ? "active" : ""}`}
                onClick={() => setActiveTab("treinos")}
              >
                üèãÔ∏è Treinos
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === "feedbacks" ? "active" : ""}`}
                onClick={() => setActiveTab("feedbacks")}
              >
                üí¨ Feedbacks
              </button>
            </li>
            <li className="nav-item">
              <button
                className={`nav-link ${activeTab === "progresso" ? "active" : ""}`}
                onClick={() => setActiveTab("progresso")}
              >
                üìà Progresso
              </button>
            </li>
          </ul>

          {/* Conte√∫do das abas */}
          <div className="mt-3">
            {activeTab === "info" && (
              <div>
                <p>
                  <strong>Email:</strong> {student.email}
                </p>
                <p>
                  <strong>Telefone:</strong> {student.phone || "-"}
                </p>
                <p>
                  <strong>Idade:</strong> {student.age || "-"}
                </p>
                <p>
                  <strong>Peso:</strong>{" "}
                  {student.weight ? `${student.weight} kg` : "-"}
                </p>
                <p>
                  <strong>Altura:</strong>{" "}
                  {student.height ? `${student.height} m` : "-"}
                </p>
                <p>
                  <strong>Objetivo:</strong> {student.goal || "-"}
                </p>
              </div>
            )}

            {/* ABA DE TREINOS */}
            {activeTab === "treinos" && (
              <div>
                <h5>üèãÔ∏è Treinos</h5>
                {loadingWorkouts ? (
                  <p>Carregando treinos...</p>
                ) : workouts.length === 0 ? (
                  <p>Nenhum treino cadastrado.</p>
                ) : (
                  <ul>
                    {workouts.map((w) => (
                      <li key={w.id}>
                        <strong>{w.title}</strong> ‚Äî {w.dayOfWeek || "Dia n√£o definido"}
                        <br />
                        <small>{w.description}</small>
                      </li>
                    ))}
                  </ul>
                )}

                <button
                  className="btn btn-outline-primary mt-2"
                  onClick={handleAddWorkout}
                >
                  + Novo Treino
                </button>
              </div>
            )}

            {activeTab === "feedbacks" && <p>Em breve...</p>}
            {activeTab === "progresso" && <p>Em breve...</p>}
          </div>
        </div>
      </div>
    </div>
  );
}
